sysauto_script.script:
//Get Current Date and Time 
var rightNow = new GlideDateTime();
var next24Hours = new GlideDateTime();
next24Hours.addDaysLocalTime(1);
// Query the database to find the needit task records which are in open state
// and due date is beween now and next 24 hours.
var soonOverdueNITasks = new GlideRecord('x_58872_needit_needit_task');
soonOverdueNITasks.addQuery('due_date' , '<',next24Hours);
soonOverdueNITasks.addQuery('due_date' , '>=',rightNow);
soonOverdueNITasks.addQuery('state','!=','3');
soonOverdueNITasks.query();

//Write log messsage for each overdue task
while(soonOverdueNITasks.next()){
	gs.info("Soon to Overdue needit task = "+ soonOverdueNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',soonOverdueNITasks,soonOverdueNITasks.number,gs.getUserDisplayName(),'soon_due_queue');
}
